import React, { useState } from "react";
import {
	AppBar,
	Avatar,
	Box,
	Button,
	Grid,
	IconButton,
	Toolbar,
	Typography,
	Dialog,
	DialogContent,
	DialogTitle,
	Drawer,
	List,
	ListItemText,
	ListItem,
	ListItemIcon,
	Menu,
	MenuItem,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/styles";
// import { useUser } from "../utils/auth/useUser";
import {
	AccountCircle,
	Book,
	ExitToApp,
	Backup,
	Favorite,
	LocalLibrary,
	EmojiObjects,
	Tune,
} from "@material-ui/icons";
import MenuIcon from "@material-ui/icons/Menu";
// import FirebaseAuth from "./FirebaseAuth";

const drawerWidth = 100;
const barWidth = 60;

const useStyles = makeStyles((theme) => ({
	root: {
		display: "flex",
	},
	appBar: {
		[theme.breakpoints.up("sm")]: {
			width: `calc(100% - ${drawerWidth}px)`,
			marginLeft: drawerWidth,
		},
	},
	menuButton: {
		[theme.breakpoints.up("sm")]: {
			display: "none",
		},
	},
	logo: {
		marginRight: theme.spacing(2),
		height: "75px",
	},
	logoContainer: {
		display: "flex",
		padding: "0",
		"&:hover": {
			cursor: "pointer",
		},
	},
	toolbar: {
		display: "flex",
		justifyContent: "space-between",
		background: "#0A5429",
		height: barWidth,
	},
	responsiveMenu: {
		[theme.breakpoints.down("sm")]: {
			display: "none",
		},
	},
	responsiveIcon: {
		display: "none",
		[theme.breakpoints.down("sm")]: {
			display: "block",
		},
	},
	centerText: {
		display: "flex",
		alignItems: "center",
	},
	menuItems: {
		color: "#EEF8F9",
	},
	menuItems1: {
		color: "#EEF8F9",
		backgroundColor: "#126500",
	},
	menuIcon: {
		marginRight: theme.spacing(0.2),
	},
	paper: {
		background: "#0A5429",
	},
	viewButtonLabel: { textTransform: "none" },
}));

const Navbar = ({currentPage}) => {
	const classes = useStyles();
	// const { user, logout } = useUser();
    const user = true;
	const [open, setOpen] = React.useState(false);

	// navbar items
	const Items = (props) => {
		const [anchorEl, setAnchorEl] = React.useState(null);
		const handleClick = (event) => {
			setAnchorEl(event.currentTarget);
		};

		const handleClose = () => {
			setAnchorEl(null);
		};

		return (
			<div>
				{user ? (
					<Toolbar disableGutters>
						<Grid container className={classes.responsiveMenu}>
							<Button href={`/profile/profile`}
								className={currentPage == 1 ? classes.menuItems1 : classes.menuItems}
								useState={{currentPage:1}}
							>
								<AccountCircle
									className={`${classes.menuIcon} ${classes.menuItems}`}
								/>
								<Typography variant="subtitle2">Profile</Typography>
							</Button>
							{/* <Button href={`/recipes/recipeListClient`}
								className={currentPage == 2 ? classes.menuItems1 : classes.menuItems}
								useState={{currentPage:2}}
							>
								<Book />
								<Typography variant="subtitle2">Recipes</Typography>
							</Button> */}
							{/* <Button
								onClick={() => logout()}
								className={`${classes.menuIcon} ${classes.menuItems}`}
							>
								<ExitToApp />
								<Typography variant="subtitle2">Logout</Typography>
							</Button> */}
						</Grid>
						<Button className={classes.responsiveIcon}>
							<MenuIcon className={classes.menuItems} onClick={handleClick} />
							<Menu
								id="simple-menu"
								anchorEl={anchorEl}
								keepMounted
								open={Boolean(anchorEl)}
								onClose={handleClose}
								classes={{ paper: classes.paper }}
							>
								<MenuItem button key={0}
									className={currentPage == 1 ? classes.menuItems1 : classes.menuItems}
								>
									<Button
										href={`/profile/profile`}
										className={classes.menuItems}
										classes={{ label: classes.viewButtonLabel }}
										useState={{currentPage:1}}
									>
										<ListItemIcon>
											<AccountCircle
												className={`${classes.menuIcon} ${classes.menuItems}`}
											/>
										</ListItemIcon>
										<Typography variant="subtitle1">Profile</Typography>
									</Button>
								</MenuItem>
								{/* <MenuItem button key={1}
									className={currentPage == 2 ? classes.menuItems1 : classes.menuItems}
								>
									<Button
										href={`/recipes/recipeListClient`}
										className={classes.menuItems}
										classes={{ label: classes.viewButtonLabel }}
										useState={{currentPage:2}}
									>
										<ListItemIcon>
											<Book
												className={`${classes.menuIcon} ${classes.menuItems}`}
											/>
										</ListItemIcon>
										<Typography variant="subtitle1">Recipes</Typography>
									</Button>
								</MenuItem> */}
								{/* <MenuItem button key={4} className={classes.menuItems}>
									<Button
										onClick={() => logout()}
										className={classes.menuItems}
										classes={{ label: classes.viewButtonLabel }}
									>
										<ListItemIcon>
											<ExitToApp
												className={`${classes.menuIcon} ${classes.menuItems}`}
											/>
										</ListItemIcon>
										<Typography variant="subtitle1">Logout</Typography>
									</Button>
								</MenuItem> */}
							</Menu>
						</Button>
					</Toolbar>
				) : (
					<Toolbar disableGutters></Toolbar>
				)}
			</div>
		);
	};

	return (
		<div>
			<div>
				<AppBar position="static">
					<Toolbar className={classes.toolbar}>
						<Button href="/" className={classes.logoContainer}>
							{/* <Avatar
								alt="Logo"
								src="/assets/eatwell_logo.png"
								className={classes.logo}
							/> */}
							<Typography
								variant="h6"
								style={{
									userSelect: "none",
									color: "#EEF8F9",
								}}
							>
								411 Music World
							</Typography>
						</Button>
						<Items />
					</Toolbar>
				</AppBar>
				<Dialog
					aria-labelledby="simple-dialog-title"
					open={open}
				>
					<DialogTitle id="form-dialog-title">Login</DialogTitle>
				</Dialog>
			</div>
		</div>
	);
};
export default Navbar;
