import React, { useState } from "react";
import {
    AppBar,
    Box,
    Toolbar,
    Typography,
    Button,
    IconButton,
    Grid
} from "@material-ui/core";
import {LibraryMusic} from '@material-ui/icons';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import LoginIcon from '@mui/icons-material/Login';
import LogoutIcon from '@mui/icons-material/Logout';
import SearchConcert from "./SearchConcert";
import Profile from "./Profile";

const Navbar = () => {
    const [currentPage, setCurrentPage] = React.useState(0);
    const [auth, setAuth] = React.useState(true);

    return (
        <Box sx={{ flexGrow: 1 }}>
          <AppBar position="static" style={{background: "linear-gradient(45deg, #fe6b8b 30%, #ff8e53 90%)"}}>
            <Toolbar>
                {!auth && (
                    <IconButton
                        size="large"
                        aria-label="account of current user"
                        aria-controls="menu-appbar"
                        aria-haspopup="true"
                        color="inherit"
                    >
                        <LibraryMusic />
                    </IconButton>
                )}
                {auth && (
                    <IconButton
                        size="large"
                        aria-label="account of current user"
                        aria-controls="menu-appbar"
                        aria-haspopup="true"
                        onClick={() => setCurrentPage(1)}
                        color="inherit"
                    >
                        <AccountCircleIcon />
                    </IconButton>
                )}
                <Typography variant="h6" component="div" sx={{ flexGrow: 1 }} onClick={() => setCurrentPage(0)}>
                    CS411 Music World!
                </Typography>
                {!auth && (
                    <IconButton
                        size="large"
                        aria-label="account of current user"
                        aria-controls="menu-appbar"
                        aria-haspopup="true"
                        onClick={() => {setCurrentPage(0); setAuth(true)}}
                        color="inherit"
                    >
                        <LoginIcon />
                        <div style={{fontSize: 18, fontWeight: 600}}>Login</div>
                    </IconButton>
                )}
                {auth && (
                    <IconButton
                        size="large"
                        aria-label="account of current user"
                        aria-controls="menu-appbar"
                        aria-haspopup="true"
                        onClick={() => {setCurrentPage(0); setAuth(false)}}
                        color="inherit"
                    >
                        <LogoutIcon />
                        <div style={{fontSize: 18, fontWeight: 600}}>Logout</div>
                    </IconButton>
                )}
            </Toolbar>
          </AppBar>
          {currentPage == 0 ?
            <SearchConcert /> : <Grid></Grid>
          }

        </Box>
      );
};
export default Navbar;
