import React, { useState, useEffect } from "react";
import {Grid, Typography, Demo, List, ListItem, ListItemText} from '@material-ui/core';

function Playlists({artistName}) {
    const [playlists, setPlaylists] = useState([]);

    useEffect(() => {
        async function fetchData() { 
            const response = await fetch(`/api/playlists/${artistName}`)
            const data = await response.json()
            var tempData = data.body.tracks.items
            var tempPlaylists = []
            console.log(artistName)
            for (var i = 0; i < tempData.length; i++) {
                console.log(tempData[i]?.album?.artists[0]?.name)
                if (tempData[i]?.album?.artists[0]?.name == artistName) {
                    tempPlaylists.push(tempData[i])
                }
            }
            console.log(data.body.tracks.items)
            setPlaylists(tempPlaylists)
        }
        fetchData()
    }, [artistName])

    return (
        <div>
            <Grid container spacing={2}>
                <Grid item xs={12} md={6}>
                <Typography sx={{ mt: 4, mb: 2 }} variant="h6" component="div">
                    Playlist
                </Typography>
                    <List dense={true}>
                        {playlists.map((playlist) => {
                            return (
                                <ListItem>
                                    <ListItemText
                                        primary={playlist?.album?.artists[0]?.name}
                                        secondary={'Secondary text'}
                                    />
                                </ListItem>)
                        })
                    }
                    </List>
                </Grid>
            </Grid>
        </div>
    );
}

export default Playlists;
